export const misProducto = [
  {
    id: "1",
    nombre: "picador",
    precio: 200,
    img: "./imagenes/picador.jpg",
    idCat: 1,
  },
  {
    id: "2",
    nombre: "seda ocb black",
    precio: 150,
    img: "./imagenes/sedaocbblack.jpg",
    idCat: 2,
  },
  {
    id: "3",
    nombre: "sustrato la black",
    precio: 300,
    img: "./imagenes/sustratolablack.jpg",
    idCat: 3,
  },
  {
    id: "4",
    nombre: "tabaco sayri",
    precio: 300,
    img: "./imagenes/tabacosayri.jpg",
    idCat: 1,
  },
];

export const getProducto = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(misProducto);
    }, 2000);
  });
};
export default getProducto;

export const getUnProducto = (id) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      const producto = misProducto.find((prod) => prod.id === id);
      resolve(producto);
    }, 100);
  });
};

// productos por categorias

export const getProductosPorCategoria = (idCat) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      const productosCategoria = misProducto.filter(
        (prod) => prod.idCat === idCat
      );
      resolve(productosCategoria);
    }, 100);
  });
};
