export const misProducto = [
  { id: 1, nombre: "picador", precio: 200, img: "./imagenes/picador.jpg" },
  {
    id: 2,
    nombre: "seda ocb black",
    precio: 150,
    img: "./imagenes/sedaocbblack.jpg",
  },
  {
    id: 3,
    nombre: "sustrato la black",
    precio: 300,
    img: "./imagenes/sustratolablack.jpg",
  },
  {
    id: 4,
    nombre: "tabaco sayri",
    precio: 300,
    img: "./imagenes/tabacosayri.jpg",
  },
];

export const getProducto = () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(misProducto);
    }, 2000);
  });
};
export default getProducto;

export const getUnProducto = (id) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      const producto = misProducto.find((prod) => prod.id === id);
      resolve(producto);
    }, 2000);
  });
};
